import gmpy2 
def legendre_symbol(n,p):
    return pow(n,(p-1)//2,p)

def tonelli_shanks(n,p):
    if n%p==0:
        return 0
    if p%4==3:
        return pow(n,(p+1)//4,p)
    q = p-1
    s = 0
    while q%2==0:
        s+=1
        q=q//2
    # print(f"q = {q}")
    # print(f"s = {s}")
    z = 2
    while legendre_symbol(z,p)==1:
        z+=1
    # print(f"z = {z}")
    m = s
    c = pow(z,q,p)
    t = pow(n,q,p)
    r = pow(n,(q+1)//2,p)
    # print(f"m = {m}") 
    # print(f"c = {c}")
    # print(f"t = {t}")
    # print(f"r = {r}")
    while t!=1:
        i = 0
        temp = t
        while temp!=1:
            i+=1
            temp = (temp*temp)%p
        expo = 2**(m-i-1)
        b = pow(c,expo,p)
        m = i
        c = (b * b)%p
        t = (t * b * b)%p
        r = (r * b)%p
    return r

